import React, { useState } from 'react';


export default function Textform(props) {
  const [Text, setText] = useState("Enter the text"); 
const uppercase=()=>{
  // console.log("Upper cass was clicked.")
  let newtext= Text.toUpperCase();
  setText(newtext)
}
const onchanged=(event)=>{
  console.log("Onchanged")
  setText(event.target.value)
}
const lowercase=()=>{
  // console.log("Lower was clicked..")
  let newtxt=Text.toLowerCase();
  setText(newtxt)
}
const clear=()=>{
  setText("")
  
}



  return (
   
   <div>
    
    <h1>{props.heading}</h1>
<div className="mb-3">

  <textarea className="form-control" value={Text} onChange={onchanged} id="box" rows="8"></textarea>
</div>
<button className="btn btn-primary" onClick={uppercase}>Convert to upercase</button>
<button className="btn btn-primary " onClick={lowercase}>Convert to lowercase</button>
<button className="btn btn-primary ml-3" onClick={clear}>Clear All</button>
<div className="container">
    <h1>YOur Text summary</h1>
    <p> {Text.split(" ").length} words and {Text.length} charactors</p>
    <p> {0.008* Text.split(" ").length} MInutes to read charactors</p>


  </div>

</div>


   )
}
